mapper.select:
SELECT a.ADVICE_NO ,spam.name ,assent.STATE ,a.ASSENT_NO ,a.CAUSE ,a.ASSENT_DATE ,a.REGISTRATION ,a.ADVICE_DATE ,a.REASON ,a.CAUSE ,a.MON ,a.TUE ,a.WED ,a.THUR ,a.FRI ,a.QUARTER FROM ADVICE a,ASSENT assent, SPAM_USER spam WHERE a.ASSENT_NO = assent.ASSENT_NO AND a.S_ID = spam.ID
AND a.QUARTER > 0
AND a.QUARTER = 0
AND a.P_ID = #{pId}
AND a.S_ID = #{sId}
AND a.ADVICE_DATE = #{adviceDate}
AND a.MON != '0'
AND a.TUE != '0'
AND a.WED != '0'
AND a.THUR != '0'
AND a.FRI != '0'
AND a.MON = #{mon}
AND a.TUE = #{tue}
AND a.WED = #{wed}
AND a.THUR = #{thur}
AND a.FRI = #{fri}
AND a.ASSENT_NO = #{assentNo}
ORDER BY a.ADVICE_DATE ASC

SELECT a.ADVICE_NO ,a.ASSENT_NO ,a.S_ID ,a.P_ID ,spam.name ,assent.STATE ,a.CAUSE ,a.ASSENT_DATE ,a.REGISTRATION ,a.ADVICE_DATE ,a.REASON ,a.CAUSE ,a.MON ,a.TUE ,a.WED ,a.THUR ,a.FRI ,a.QUARTER FROM ADVICE a,ASSENT assent, SPAM_USER spam WHERE a.ASSENT_NO = assent.ASSENT_NO AND a.P_ID = spam.ID
AND a.QUARTER > 0
AND a.QUARTER = 0
AND a.P_ID = #{pId}
AND a.S_ID = #{sId}
AND a.ADVICE_DATE = #{adviceDate}
AND MON != '0'
AND TUE != '0'
AND WED != '0'
AND THUR != '0'
AND FRI != '0'
AND MON = #{mon}
AND TUE = #{tue}
AND WED = #{wed}
AND THUR = #{thur}
AND FRI = #{fri}
AND a.ASSENT_NO = #{assentNo}
ORDER BY a.ADVICE_DATE ASC

mapper.update:
UPDATE ADVICE

  ASSENT_NO = #{assentNo},   
  CAUSE = #{cause},	  
  ASSENT_DATE = #{assentDate} 


WHERE  ADVICE_NO = #{adviceNo}
