mapper.select:
SELECT a.ADVICE_NO ,a.S_ID ,a.P_ID ,assent.STATE ,a.CAUSE ,a.ASSENT_DATE ,a.REGISTRATION ,a.ADVICE_DATE ,a.REASON ,a.CAUSE ,a.MON ,a.TUE ,a.WED ,a.THUR ,a.FRI ,a.QUARTER FROM ADVICE a INNER JOIN ASSENT assent ON a.ASSENT_NO = assent.ASSENT_NO WHERE a.ADVICE_DATE > SYSDATE-1
AND a.QUARTER > 0
AND a.QUARTER = 0
AND P_ID = #{pId}
AND S_ID = #{sId}
AND ADVICE_DATE = #{adviceDate}
AND MON != '0'
AND TUE != '0'
AND WED != '0'
AND THUR != '0'
AND FRI != '0'
AND MON = #{mon}
AND TUE = #{tue}
AND WED = #{wed}
AND THUR = #{thur}
AND FRI = #{fri}
ORDER BY ADVICE_DATE ASC

SELECT a.ADVICE_NO ,a.S_ID ,a.P_ID ,assent.STATE ,a.CAUSE ,a.ASSENT_DATE ,a.REGISTRATION ,a.ADVICE_DATE ,a.REASON ,a.MON ,a.TUE ,a.WED ,a.THUR ,a.FRI ,a.QUARTER FROM ADVICE a INNER JOIN ASSENT assent ON a.ASSENT_NO = assent.ASSENT_NO WHERE ADVICE_NO = #{adviceNo}
AND QUARTER = #{quarter}
AND MON = #{mon}
AND TUE = #{tue}
AND WED = #{wed}
AND THUR = #{thur}
AND FRI = #{fri}

mapper.update:
UPDATE ADVICE

  ASSENT_NO = #{assentNo},   
  CAUSE = #{cause},	  
  ASSENT_DATE = #{assentDate} 


WHERE  ADVICE_NO = #{adviceNo}
