mapper.select:
SELECT SCHEDULE_NO
	  ,ID
	  ,REASON
	  ,BEGIN
	  ,FINISH
	  ,MON
	  ,TUE
	  ,WED
	  ,THUR
	  ,FRI
	  ,QUARTER
FROM SCHEDULE




			AND SCHEDULE_NO = #{scheduleNo}


			AND QUARTER = #{quarter}


			AND MON = #{mon}


			AND TUE = #{tue}


			AND WED = #{wed}


			AND THUR = #{thur}


			AND FRI = #{fri}


ORDER BY BEGIN ASC

mapper.update:
UPDATE ADVICE
ASSENT_NO = #{assentNo}, CAUSE = #{cause}, ASSENT_DATE = #{assentDate},
WHERE ADVICE_NO = #{adviceNo}
