mapper.select:
SELECT   ID
	   , PASSWORD
	   , POWER
	   , NAME 
	   , GRADE
	   , MAJOR
	   , PHONE_NO
	   , BIRTH_DATE
	   , OFFICE
	   , EMP_FORM
	   , GR_FORM
	   , CARD_NO
	   , ENROLLMENT
FROM SPAM_USER

	
		AND ID = #{id}
	
	
		AND PASSWORD = #{passWord}
	
	
		AND POWER = #{power}
	
	
		AND NAME = #{name}
	
	
		AND BIRTH_DATE = #{birthDate}
	
	
		AND OFFICE = #{office}
	
	
		AND EMP_FORM = #{empForm}
	
	
		AND GR_FORM = #{grForm}
	
	
		AND CARD_NO = #{cardNo}
	
	
		AND ENROLLMENT = #{enrollment}

mapper.update:
INSERT INTO SPAM_USER 
(ID, PASSWORD, POWER, NAME, GRADE, MAJOR, PHONE_NO, BIRTH_DATE, OFFICE, EMP_FORM, GR_FORM, CARD_NO, ENROLLMENT)
VALUES (#{id}
	   ,#{passWord}
	   ,#{power}
	   ,#{name}
	   ,#{grade}
	   ,#{major}
	   ,#{phoneNo}
	   ,#{birthDate}
	   ,#{office}
	   ,#{empForm}
	   ,#{grForm}
	   ,#{cardNo}
	   ,#{enrollment}
)
