mapper.select:
SELECT   ID
	   , PASSWORD
	   , POWER
	   , NAME 
	   , GRADE
	   , MAJOR
	   , PHONE_NO
	   , BIRTH_DATE
	   , OFFICE
	   , EMP_NO
	   , GR_NO
	   , CARD_NO
	   , ENROLLMENT
FROM SPAM_USER

	
		AND ID = #{id}
	
	
		AND PASSWORD = #{passWord}
	
	
		AND POWER = #{power}
	
	
		AND NAME = #{name}
	
	
		AND BIRTH_DATE = #{birthDate}
	
	
		AND OFFICE = #{office}
	
	
		AND EMP_NO = #{empNo}
	
	
		AND GR_NO = #{grNo}
	
	
		AND CARD_NO = #{cardNo}
	
	
		AND ENROLLMENT = #{enrollment}

SELECT   ID
	   , PASSWORD
	   , POWER
	   , NAME 
	   , GRADE
	   , MAJOR
	   , PHONE_NO
	   , BIRTH_DATE
	   , OFFICE
	   , EMP_NO
	   , GR_NO
	   , CARD_NO
	   , ENROLLMENT
FROM SPAM_USER

	
		AND ID = #{id}
	
	
		AND PASSWORD = #{passWord}
	
	
		AND POWER = #{power}
	
	
		AND NAME = #{name}
	
	
		AND BIRTH_DATE = #{birthDate}
	
	
		AND OFFICE = #{office}
	
	
		AND EMP_NO = #{empNo}
	
	
		AND GR_NO = #{grNo}
	
	
		AND CARD_NO = #{cardNo}
	
	
		AND ENROLLMENT = #{enrollment}

mapper.insert:
INSERT INTO SPAM_USER 
